INSERT INTO "public"."modules"("duration", "course_id", "description", "title", "you_will_learn", "you_will_learn_title", "created_at", "updated_at", "id", "next_module_id", "previous_module_id") VALUES (0, E'9df7cd5f-d9b3-4f37-9693-15ed1be65cf5', null, E'The origins of generative art', null, null, E'2024-08-27T01:00:03.791419+00:00', E'2024-08-27T01:00:03.791419+00:00', E'4c02988e-e748-4fab-b0e4-fbcb50d063b9', null, E'3c863d7e-cf4c-4dfd-8c98-fa117cee3908');
INSERT INTO "public"."modules"("duration", "course_id", "description", "title", "you_will_learn", "you_will_learn_title", "created_at", "updated_at", "id", "next_module_id", "previous_module_id") VALUES (0, E'9df7cd5f-d9b3-4f37-9693-15ed1be65cf5', E'Contemporary generative art, spanning from the 2000s to the present, has seen a proliferation of experimentation, innovation, and interdisciplinary collaboration. Artists harnessed advanced computational techniques, artificial intelligence, and real-time data to create dynamic artworks that challenge traditional notions of authorship, creativity, and the role of the artist.', E'Contemporary generative art', null, null, E'2024-08-27T17:03:19.66698+00:00', E'2024-08-27T17:03:19.66698+00:00', E'4674105d-8dca-49d6-a705-512e66db9dfd', null, E'4c02988e-e748-4fab-b0e4-fbcb50d063b9');
